import React, { useState } from 'react';

function App() {
  const [formData, setFormData] = useState({
    publisher_name: '',
    publisher_url: '',
    topic: '',
    keywords: '',
    context: '',
    supporting_data: '',
    image_url: '',
  });
  const [imagePreview, setImagePreview] = useState(null);
  const [generatedContent, setGeneratedContent] = useState(null);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));

    // Handle image preview logic
    if (name === 'image_url') {
      setImagePreview(value); // Set the image URL for preview
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null); // Clear any previous errors
    try {
      const response = await fetch('http://127.0.0.1:5000/api/generate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error('Failed to generate article');
      }

      const data = await response.json();
      setGeneratedContent(data);
    } catch (err) {
      setError(err.message);
    }
  };

  const downloadArticle = async () => {
    try {
      const response = await fetch('http://127.0.0.1:5000/api/download_article', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(generatedContent),
      });

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `${generatedContent.headline}.html`;
      a.click();
    } catch (error) {
      setError('Failed to download article');
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial' }}>
      <h1>AI Article Generator</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Publisher Name:</label>
          <input
            type="text"
            name="publisher_name"
            value={formData.publisher_name}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Publisher URL:</label>
          <input
            type="url"
            name="publisher_url"
            value={formData.publisher_url}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Topic:</label>
          <textarea
            name="topic"
            value={formData.topic}
            onChange={handleChange}
            required
          ></textarea>
        </div>
        <div>
          <label>SEO Keywords:</label>
          <input
            type="text"
            name="keywords"
            value={formData.keywords}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Context:</label>
          <textarea
            name="context"
            value={formData.context}
            onChange={handleChange}
            required
          ></textarea>
        </div>
        <div>
          <label>Supporting Data:</label>
          <textarea
            name="supporting_data"
            value={formData.supporting_data}
            onChange={handleChange}
            required
          ></textarea>
        </div>
        <div>
          <label>Image URL:</label>
          <input
            type="url"
            name="image_url"
            value={formData.image_url}
            onChange={handleChange}
          />
          {/* Preview the image */}
          {imagePreview && (
            <div style={{ marginTop: '10px' }}>
              <img
                src={imagePreview}
                alt="Image Preview"
                style={{ maxWidth: '300px', maxHeight: '200px', objectFit: 'contain' }}
                onError={() => setImagePreview(null)} // Hide preview if URL is invalid
              />
            </div>
          )}
        </div>
        <button type="submit">Generate Article</button>
      </form>

      {error && <p style={{ color: 'red' }}>Error: {error}</p>}

      {generatedContent && (
        <div>
          <h2>Generated Article</h2>
          <p><strong>Headline:</strong> {generatedContent.headline}</p>
          <div dangerouslySetInnerHTML={{ __html: generatedContent.article_content }} />
          <button onClick={downloadArticle}>Download Article</button>
        </div>
      )}
    </div>
  );
}

export default App;
